mapper.insert:
INSERT INTO board ( bdWriter
                  , bdTitle
                  , bdContents
                  )
           VALUES ( #{bdWriter}
                  , #{bdTitle}
                  , #{bdContents}
                  )

    SELECT LAST_INSERT_ID()

mapper.update:
UPDATE board
   SET bdWriter = #{bdWriter}
     , bdTitle = #{bdTitle}
     , bdContents = #{bdContents}
 WHERE bdSeq = #{bdSeq}
